@js:
var star=java.getString("#readbtn@a@href")
var start=star.match(/\/p(\d+).html/)[1]
start=Number(start)
var end=java.getString("class.u-chapter@li.0@a@href")
var ends=result.match(/\/p(\d+).html/)[1]
ends=Number(ends)
let list=[]
if(ends-start<10000){
	for(i=start;start<=ends;i++,start++){
		name=`第${i}章`
		link=String(star).replace(/\/p(\d+).html/,`\/p${i}.html`)
		list.push({text:name,href:link})
		list;
		}
	}else{
 java.toast("目录异常，章节列表获取失败！")
 list=[{text:"正文",href:star}];
}
 list;

