let v = [],
		list = [];
JSON.parse(result).list.map($=>{

//分卷判定
		V = $.name;
		if(v[v.length-1]!=V&&!/^\s*$/.test(V)){
				v.push(V)
				list.push({
						name: '📖['+V+']📖',
						volume: true
					})
			}

$.bookChapters.map($=>{
		return list.push({
