loginUrl:
try {
    [uri, t1, t2] = String(java.hexDecodeToString(result)).split(type);

    headers = cache.get(type + 'Headers');
    if (headers == null) {
        headers = {
            'Referer': host
        };
        C = java.head(host + 'favicon.ico?v=1', headers).cookies();
        e = Object.keys(C)[0];
        t = unescape(C[e]);
        headers.Cookie = e + '=' + C[e];
        headers.Secret = java.webView(Secret(e, t), null, "Secret");
        headers = JSON.stringify({
            headers: headers
        });
        cache.put(type + 'Headers', headers, 300);
    }

    durl = uri => `data:;base64,${java.base64Encode(uri)},{"type":"${type}"}`;
    jurl = (uri, rn, v1) => `${host}api/${v1||""}www/${uri}&rn=${rn}&httpsStatus=1&reqId=${java.randomUUID()}&plat=web_www&from=,${headers}`;
    ajax_data = (url, ajaxerr) => {
        try {
            return JSON.parse(java.ajax(url)).data;
        } catch (err) {
            return ajaxerr < 2 ? ajax_data(url, ajaxerr + 1) : {};
        }
    }
    run = (rn, v1) => data = ajax_data(jurl(uri, rn, v1), 0);
    file_data = $id => {
        list = [];
        flist = eval(String(source.bookSourceComment).replace(/\[(file|ajax|js)\]([\s\S]+?)\[\/\1\]/g, ($0, $1, $2) => {
            try {
                switch ($1) {
                    case 'file':
                        return java.importScript($2);
                        break;
                    case 'ajax':
                        return java.ajax($2.trim());
                        break;
                    case 'js':
                        return eval($2);
                        break;
                }
            } catch (err) {
                return "";
            }
        }).replace(/(\]|})\s*,\s*(\]|})/g, '$1$2').replace(/}\s*{/g, '},{'));
        for ($ of flist) {
            if ($.id) {
                list.push($);
            } else {
                if ($id) {
                    if (_ = $[$id]) {
                        _.fid = $id;
                        _.total = _.musicList.length;
                        return _;
                    }
                } else {
                    for (fid of Object.keys($)) {
                        _ = $[fid];
                        _.fid = fid;
                        if (_.musicList && _.musicList.length)
                            _.total = _.musicList.length,
                            list.push(_);
                    }
                }
            }
        };
        if ($id) return java.toast("没找到对应资源"), {};
        return list;
    };
    switch (t1) {


        case '搜索':
            if (t2)
                data = [run(1)];
            else
                data = run(30).list;
            data = data.map($ => {
                $.bookUrl = durl(`playlist/playListInfo?pid=${$.id}&pn=1${type}详情`);
                return $;
            });
            break;


        case '发现':
            if (t2)
                data = file_data();
            else
                data = run(30),
                data = data.data || data.list;
            data = data.map($ => {
                $uri = $.fid || `playlist/playListInfo?pid=${$.id}&pn=1`;
                $.bookUrl = durl(`${$uri+type}详情${$.fid?type+"本地":""}`);
                return $;
            });
            break;


        case '加载':
            if (t2)
                data = JSON.parse(java.base64Decode(uri)).map($ => {
                    $uri = `bang/bang/musicList?bangId=${$.sid}&pn=1`;
                    $.bookUrl = durl($uri + type + "详情");
                    return $;
                });
            else
                data = run(1);
            break;


        case '详情':
            if (t2)
                data = file_data(uri);
            else
                data = run(1);
            data.tocUrl = durl(uri + type + "目录" + (data.fid ? type + "本地" : ""));
            break;


        case '目录':
            if (t2)
                data = file_data(uri);
            else {
                data = run(30);

                // 目录下一页
                p = data.total || data.num;
                if (p > 30) {
                    i = +uri.match(/pn=(\d+)/)[1];
                    if (p > 30 * i)
                        java.put("nextTocUrl", durl(uri.replace('pn=' + i, 'pn=' + (i + 1)) + type + "目录"));
                }
            }
            data = data.musicList.map($ => {
                return {
                    name: $.name,
                    zyid: durl($.rid + type + "正文" + ($.isListenFee ? type + 'vip' : '')),
                    info: `🔖 ${$.isListenFee?'Paid':'Free'}Music　📼 ${$.songTimeMinutes}　🎻 ` + $.artist,
                    vip: $.isListenFee
                };
            });
            break;


        case '正文':
            data = {};
            try {
                if (t2) throw new Error("is vipmusic");
                uri = "music/playUrl?mid=" + uri + "&type=music&";
                url = run(1, 'v1/').url;
            } catch (err) {
                q = java.webView(script(uri), null, "$exports");
                url = 'http://mobi.kuwo.cn/mobi.s?f=kuwo&q=' + q;
                url = java.get(url, {
                    "User-Agent": "okhttp/3.10.0"
                }).body().match(/url=(\S+)/)[1];
            }
            break;


    }
    JSON.stringify(data);
} catch (Error) {
    try {
        [uri, t1, t2] = String(java.hexDecodeToString(result)).split(type);
    } catch (Login) {


        Arr = ["推荐", "排行", "主题", "心情", "场景", "年代", "曲风", "语言"];

        function login(t) {
            if (t === undefined) return true;
            java.toast(t);
            java.log(t);
        }
        setv = json => source.setVariable(JSON.stringify(json));
        yn = yn => yn ? '启用' : '禁用';
        $$ = (type) => {
            t2 = type;
            if (type == '全部') {
                type = "推荐"
                for (key of Arr.slice(1))
                    $[key] = !$[type];
            }
            login('发现设置：\n【' + t2 + '】 ' + yn($[type]) + ' => ' + yn($[type] = !$[type]));
            return setv($);
        }
        $ = function() {
            try {
                return JSON.parse(source.getVariable());
            } catch (err) {
                return null;
            }
        }();
        if ($ == null) {
            $ = {};
            for (key of Arr)
                $[key] = true;
            setv($);
        }
    }
}

