searchUrl:
@js:
var rHost = baseUrl;
var eUrl = rHost + "/sscc/";
var eHtml = java.ajax(eUrl).toString();
var c2List = eHtml.match(/c2="(\w{16})"/);
if (c2List != null) {
    var c2 = c2List[1];
    var jsurl = rHost + eHtml.match(/src="(\/nnxswnn\/.*)"/)[1];
    var jsText = String(java.ajax(jsurl));
    var runJs = jsText.replace(/Ajax\.open.*/, 'var cookieUrl=rHost+ajax(c2);');
    eval(runJs);
    java.ajax(cookieUrl);
}
var act = java.ajax(rHost).toString().match(/value="(\w{16})"/)[1];
var option = {
    "charset": "gb2312",
    "method": "POST",
    "body": "act=" + act + "&q={{key}}&submit=快速搜书"
}
eUrl + "," + JSON.stringify(option)

