let banben="1.0.2";
java.toast('\næ„å»ºåˆ†ç±»ä¸­');
let webr=java.ajax(surl()+'/sort/1');
webr = org.jsoup.Jsoup.parse(webr);
let lb = webr.select('.type-list li a');
let obj={};
for (char of lb) {
	obj[char.text()]={url: `surl()+"${char.attr('href')}"`}
	}
let sort = [];
ph = (title, url, size, wrap, cc) => sort.push({
    "title": title,
    "url": '{{'+url+'}}',
