//java.log(JSON.stringify(page))

let nums=[];
page.forEach(function(number){
  let match=number.match(/\d+/);
  if(match){
    nums.push(parseInt(match[0],10));
  }
})

let max=nums.slice(-1)
//java.log(JSON.stringify(max))

let list=[];
for(let i=0;i<=max;i++){
  let url=`https://www.fanqxsw.com/index.php?action=loadChapterPage,{"body":"id=${u}&page=${i}","method":"POST"}`;
  list.push(url);
  //java.log(JSON.stringify(list))
}

let lt=java.ajaxAll(list).map(x=>x.body())
//java.log(JSON.stringify(lt))

lt.reduce((arr,item)=>{
 arr=arr.concat(JSON.parse(item).data);
	return arr
},[])

