let elements = org.jsoup.Jsoup.parse(result).select("a");
let dataList = [];
elements.forEach(element => {
    let href = String(element.attr("href")).replace(/.*\('(\d+)'\,'(\d+)'\).*/,"/book/$1/$2.html")
    let text = element.text();
    let match = href.match(/(\d+)\.html/);
    if (match) {
        dataList.push({
            href: href,
            cid: parseInt(match[1]),
            text: text
        });
    }
});

dataList.sort((a, b) => a.cid - b.cid);
result = dataList.map(item => ({
    href: item.href,
    text: item.text
}));

