function generateRequest(path, signParams, bodyParams, baseUrl, secretKey) {
  const { java } = this;
  baseUrl = baseUrl || "http://a.lc1001.com";
  secretKey = secretKey || "XKrqBSeeEwgDy2pT";
  bodyParams = bodyParams || {};

  // ç”Ÿæˆæ—¶é—´æˆ³
  var timestamp = Date.now().toString();
  signParams.timestamp = timestamp;
  signParams.consumerKey = "LCREAD_ANDROID";

  // å¯¹é”®è¿›è¡Œæ’åºå¹¶æ„å»ºæŸ¥è¯¢å­—ç¬¦ä¸²
  var keys = Object.keys(signParams).sort();
  var queryString = "";
  for (var i = 0; i < keys.length; i++) {
    queryString += keys[i] + "%3D" + signParams[keys[i]];
  }

  // æ„å»ºç­¾åæ•°æ®å­—ç¬¦ä¸²
  var signData = "GET" + encodeURIComponent(baseUrl + path) + queryString + secretKey;
  java.log(signData)
  // ç”Ÿæˆç­¾å
  var sign = java.md5Encode(signData);

  // åˆ›å»ºå®Œæ•´çš„è¯·æ±‚å‚æ•°(åŒ…å«ç­¾åå‚æ•°å’Œbodyå‚æ•°)
  var allParams = {};

  // å¤åˆ¶ç­¾åå‚æ•°
  for (var key in signParams) {
    if (signParams.hasOwnProperty(key)) {
      allParams[key] = signParams[key];
    }
  }

  // æ·»åŠ ç­¾å
  allParams.sign = sign;

  // æ·»åŠ bodyä¸“ç”¨å‚æ•°
  for (var key in bodyParams) {
    if (bodyParams.hasOwnProperty(key)) {
      allParams[key] = bodyParams[key];
    }
  }

  // æ„å»ºè¯·æ±‚ä½“
  var body = "";
  for (var key in allParams) {
    if (allParams.hasOwnProperty(key)) {
      if (body) body += "&";
      body += key + "=" + allParams[key];
    }
  }

  // è¿”å›å®Œæ•´è¯·æ±‚URLå’Œé…ç½®
  return baseUrl + path + ",{\n  \"body\": \"" + body + "\",\n  \"method\": \"POST\"\n}";
}

function bookRequest(apiPath, bid, extraParams, bodyParams, uid) {
  extraParams = extraParams || {};
  bodyParams = bodyParams || {};
  uid = uid || "0";

  var signParams = {
    uID: uid,
  };
  if (/^info|^book/.test(apiPath)) {
  	signParams.bID = bid
  }

  // æ·»åŠ é¢å¤–ç­¾åå‚æ•°
  for (var key in extraParams) {
    if (extraParams.hasOwnProperty(key)) {
      signParams[key] = extraParams[key];
    }
  }

  return this.generateRequest("/app/" + apiPath, signParams, bodyParams);
}

