function getUrl(path, type, id1, id2, filterType, categoryPath) {
  const { java } = this;
  const COMMON_PARAMS = 'lang=cn-zh&device=android&version=3.2.19';
  const headers = {
    ":authority": "czbooks.app",
    ":method": "GET",
    ":path": `/retry?path=${path}`,
    ":scheme": "https",
    "content-type": "application/x-www-form-urlencoded",
    "user-agent": java.getWebViewUA(),
    "accept": "application/vnd.api.v4+json",
    "retrycookie": "blackcat_SESSID=",
    "retryhost": "api.czbooks.net",
    "retrypath": (() => {
      switch(type) {
        case "search": 
          return `/novel/search?search={{java.encodeURI(key)}}&page={{page}}&${COMMON_PARAMS}`;
        case "detail":
          return `/novel/detail?novelId=${id1}&${COMMON_PARAMS}`;
        case "content":
          return `/chapter/detail?novelId=${id1}&chapterId=${id2}&${COMMON_PARAMS}`;
        case "find":
          return `/novel/list?type=${filterType}&category=${categoryPath}&page={{page}}&limit=100&${COMMON_PARAMS}`;
      }
    })()
  };
  return `https://czbooks.app/retry?path=${path},${JSON.stringify({ headers })}`;
}

