  if (!match) return 'ğŸ•°0ç§’';

  // è½¬æ¢ä¸ºç§’æ•°å¹¶ç¡®ä¿ä¸ºæ•´æ•°
  const totalSeconds = Math.round(parseInt(match[0], 10) / 1000);
  if (totalSeconds <= 0) return 'ğŸ•°0ç§’';

  // è®¡ç®—å„æ—¶é—´å•ä½
  const hours = Math.floor(totalSeconds / 3600);
  const minutes = Math.floor((totalSeconds % 3600) / 60);
  const seconds = totalSeconds % 60;

  // æ„å»ºæ—¶é—´å­—ç¬¦ä¸²
