let e = source.getVariable().toString();
var javaImport = new JavaImporter(
  Packages.java.security.SecureRandom
);

function randomHexString(nBytes) {
  with (javaImport) {
    var rnd = new SecureRandom();
    var bytes = rnd.generateSeed(nBytes);

    var hex = '';
    for (var i = 0; i < bytes.length; i++) {
      var b = bytes[i] & 0xFF;
      var h = b.toString(16);
      if (h.length === 1) h = '0' + h;
      hex += h;
    }
    return hex;
  }
}

    function mn(request, secretKey) {
        var timestamp = Date.now();
        var nonce = randomHexString(16).toString();

        var method = "GET"
        var path = java.toURL(request).pathname;

        var signStr = [
            method,
            path,
            JSON.stringify({}),
            JSON.stringify({}),
            timestamp,
            nonce
        ].join("|");
        //java.log(signStr);

        var signature = java.HMacHex(signStr,"HmacSHA256", secretKey).toString();

        return e+";" + signature + ";" + timestamp + ";" + nonce;
    }

    let result;
    if (e == null || e.trim() == "") {
        result = u;
    } else {
        let A = mn(u, e);
        result = `${u},{"headers":{"Authorization":"${A}"}}`;
    }
    result

