function login() {
  const loginInfo = source.getLoginInfoMap();
  const username = loginInfo.get("账号");
  const password = loginInfo.get("密码");
  const timestamp = Date.now().toString();
  // 生成16位随机ID
  const aid = String(Packages.java.util.UUID.randomUUID())
    .replace(/-/g, "")
    .slice(0, 16);

  try {
    java.toast("正在登录...请稍候");

    // 构造待加密字符串
    const data = `GEThttp%3A%2F%2Fa.lc1001.com%2Fapp%2Fsso%2FpwdLogOnconsumerKey%3DLCREAD_ANDROIDpcd%3D${username}pwd%3D${password}timestamp%3D${timestamp}XKrqBSeeEwgDy2pT`;
    const sign = java.md5Encode(data);

    // 构造登录请求 URL
    const loginUrl = `http://a.lc1001.com/app/sso/pwdLogOn?consumerKey=LCREAD_ANDROID&timestamp=${timestamp}&sign=${sign}&pcd=${username}&pwd=${password}&mType=XIAOMI-23049RAD8C&PACKINGCHANNEL=WANDOUJIA&imei=${aid}&androidID=${aid}`;
    const response = java.get(loginUrl, {});
    const responseBody = response.body();

    if (responseBody.includes("UNAME")) {
      java.log("登录成功");
      source.setVariable(responseBody);
      java.toast("✅登录成功");
    } else {
      java.log("❌登录失败，服务器返回: " + responseBody);
    }
  } catch (error) {
