bid = java.get("bid");
try {
  uid = JSON.parse(source.getVariable()).DATA.UID;
  token = JSON.parse(source.getVariable()).DATA.TOKEN;
} catch(e) {
  var uid, token
}

data.reduce(function(arr, item) {
  arr.push({"text": item.VNAME, "isvo": true});

  item.CHAPTERS.forEach(function(x) {
    cid = x.CID;
    var apiPath = x.ISVC == 0 ? "book/pubchapter" : "book/vipchapter";
    var extraParams = {cID: cid};
    var bodyParams = {};

    if (x.ISVC != 0) {
      bodyParams.token = token;
    }

    var u = bookRequest(apiPath, bid, extraParams, bodyParams, uid);
    arr.push({"text": x.CNAME, "href": u, "isvi": x.ISVC});
  });

  return arr;
}, []);

