jsLib:
/**
 * 合并书源变量（支持单键值对和批量操作）
 * @param {string|Object} key - 键名字符串 或 键值对对象
 * @param {*} [value] - 当第一个参数为string时生效的值
 * 
 * @example // 单次设置
 * mergeDataArgument("page", 1);
 * 
 * @example // 批量设置
 * mergeDataArgument({page: 1, size: 20});
 */
function mergeDataArgument(key, value) {
    // 1. 获取当前变量（兼容空/null/undefined/非JSON）
    var currentValue;
    try {
        var storedValue = this.source.getVariable();
        // 如果存储的值是空/null/undefined/"undefined"/"null"/"def"，初始化为 {}
        if (!storedValue || storedValue === "null" || storedValue === "undefined" || storedValue === "def") {
            currentValue = {};
        } else {
            currentValue = JSON.parse(storedValue);
        }
    } catch (e) {
        // 如果解析失败（非JSON），也初始化为 {}
        currentValue = {};
    }

    // 2. 处理数据合并
    if (typeof key === 'object' && key !== null) {
        // 批量模式：遍历对象，覆盖或新增键值对
        for (var k in key) {
            if (key.hasOwnProperty(k)) {
                currentValue[k] = key[k]; // 直接覆盖（如果已存在）或新增（如果不存在）
            }
        }
    } else if (typeof key === 'string') {
        // 单键模式：直接设置（覆盖或新增）
        currentValue[key] = value;
    }

    // 3. 存储回公开变量
    this.source.setVariable(JSON.stringify(currentValue));
    this.java.toast("\n✅ 设置成功");
}




/**
 * 从书源变量中获取指定键的参数值
 * @param {string} key - 要获取的参数键名
 * @param {*} [defaultValue] - 可选默认值（当键不存在时返回）
 * @returns {*} 返回对应键的值，若不存在则返回默认值或undefined
 * 
 * @example // 基础用法
 * getArgument("username"); // 返回对应的值或undefined
 * 
 * @example // 带默认值用法
 * getArgument("timeout", 30); // 不存在时返回30
 */
function getArgument(key, defaultValue) {
    // 获取并解析共享变量
    const jsonString = this.source.getVariable() || "{}";

    try {
        const data = JSON.parse(jsonString);

        // 使用in运算符检查属性存在性（包含继承属性）
        if (key in data) {
            return data[key];
        }

        // 键不存在时的统一提示
        this.java.longToast(`\n❌书源变量中没有\n"${key}"\n这个键`);
        return defaultValue;

    } catch (e) {
        // JSON解析失败时的静默处理
        this.java.longToast("\n❌书源变量解析失败");
        return defaultValue;
    }
}

