ruleContent.content:
[type="text/javascript"]@html
@js:
if (book.durChapterIndex === chapter.index){
matchedJson = result.match(/{.+}/)?.[0];
uu = JSON.parse(matchedJson);
url = uu.url;
var html = `data:text/html;charset=utf-8,<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>${book.durChapterTitle}</title>
</head>

<body>
    <video controls
        src="${url}"> width="100%" height="285px" controls="" autoplay=""
    </video>

  </body>

</html>`;
java.startBrowser(html,book.durChapterTitle);
`\n❗️如果未进入播放界面\n✅刷新或者点击章节`
}

