function login(){
var une = source.getLoginInfoMap().get("账号");
var pwd = source.getLoginInfoMap().get("密码");
  var url = `https://android-api.xrzww.com/api/login?user_name=${une}&user_password=${pwd}`;
  var token = JSON.parse(java.ajax(url));
  var header = JSON.stringify({
  	"Authorization": String("Bearer " + token.data.token)
});
source.putLoginHeader(header)
}

