(() => {
    od = src.match(/originalOrder = (\[.+?\])/);
    x = Array.from(java.getElements("class.BCsectionTwo-top-chapter@a"))
    if (od) {
        od = JSON.parse(od[1]).reduce((arr, item)=>{
        	arr.push(item +1)
        	return arr
        	}, [])
        return x.sort((a, b)=>{
         	ia = x.indexOf(a)
         	ib = x.indexOf(b)
         return od[ia]-od[ib]
        	})
    }
    return x
})();

