 let url = `http://www.txtnovel.vip/plugin.php?id=dsu_paulsign:sign&mobile=yes`;
            let res = String(java.ajax(url));

            let formhash = res.match(/<input type=\"hidden\" name=\"formhash\" value=\"(.+?)\" \/>/)[1]; 

