}

function sign() {
    let url = `http://www.txtnovel.vip/plugin.php?id=dsu_paulsign:sign&operation=qiandao&infloat=0&inajax=0&mobile=yes`;
            let body = `formhash=${getformhash()}&qdxq=kx`;
            option = {
  "method": "POST",
  "body":String(body)
}
            let res = java.ajax(url+","+JSON.stringify(option));

            let message = String(res).match(/<div id=\"messagetext\">.*?<p>(.+?)<\/p>|<div class="c">([^<]+)</);

            if (message) {
                re = message[1]+message[2];
                re = String(re).replace(/undefined|恭喜.|随机金币/g,'');
            }
            else {
                re = "签到失败\n"
            }
            java.toast(re)
}

