var variable = {domain: "", domains: []};
try {
  variable = JSON.parse(source.getVariable());
} catch(e) {
  setVariable(variable);
}
function setVariable(obj) {
  source.setVariable(JSON.stringify(obj, null, 2));
}
var header = {
  "User-Agent": "Mozilla/5.0 (Linux; Android 8.0.0) AppleWebKit/537.36 (KHTML, like Gecko) Mobile Safari/537.36"
};
function getDomains() {
  java.toast("获取线路列表...");
  try {
    var res = java.get("https://wnlink.ru/", header);
    if (res.statusCode()==200) {
      var domains = res.body().match(/[^>]+(?=<\/i><\/a>)/g);
      variable.domains = domains;
      setVariable(variable);
      java.toast("获取线路成功\n" + domains.join("\n"));
    } else {
      java.toast("获取线路失败\n" + res.statusCode());
    }
  } catch(e) {
    java.toast("获取线路失败\n" + e.message);
  }
}
function setDomain(auto) {
  if (variable.domains.length==0) {
    java.toast("当前域名为空，将自动获取线路列表...");
    getDomains();
  }
  if (!auto) {
    var i = +result["输入线路序号 (0 ~ 3)"];
    if (i >= variable.domains.length) {
      i = variable.domains.length - 1;
    }
    variable.domain = variable.domains[i];
    setVariable(variable);
    java.toast("手动设置域名成功\n" + variable.domain);
    return variable.domain;
  }
  java.toast("自动更新线路列表，并设置域名...");
  getDomains();
  for (var domain of variable.domains) {
    try {
      var res = java.head("https://"+domain, header);
      if (res.statusCode()==200) {
        variable.domain = domain;
        setVariable(variable);
        java.toast("自动设置域名成功\n" + domain);
        return domain;
      } else {
        java.toast("访问域名失败\n" + domain + "\n" + res.statusCode());
      }
    } catch(e) {
      java.toast("访问域名失败\n" + domain + "\n" + e.message);
    }
  }
}
function getDomain() {
  java.longToast("当前域名为\n" + variable.domain + "\n共有" + variable.domains.length + "条线路\n" + variable.domains.join("\n"));
}
function login() {
  var info = source.getLoginInfoMap();
  try {
    if ("登录账号" in result) info = result;
  } catch(e) {}
  var user = String(info.get("登录账号"));
  var pass = String(info.get("登录密码"));
  if (!user || !pass) {
    java.toast("未输入账号或密码");
    throw new Error("未输入账号或密码");
  }
  var body = "normal=1&login_name="+ user + "&login_pass=" + pass;
  try {
    var res = java.post("https://"+variable.domain+"/users-check_login.html", body, header);
    if (res.statusCode()==200) {
      var json = JSON.parse(res.body());
      if (json.ret==false) {
        java.toast(json.html);
        throw new Error(json.html);
      }
      var ck = JSON.stringify({
        "Cookie": res.cookies().entrySet().toArray().join("; ")
      });
      source.putLoginHeader(ck);
      java.toast(user + " 登录成功");
    } else {
      java.toast("登录失败\n" + res.statusCode());
      throw new Error("登录失败\n" + res.statusCode());
    }
  } catch(e) {
    java.toast("登录失败\n" + e.message);
    throw new Error("登录失败\n" + e.message);
  }
}
if (variable.domains.length==0) {
