function userlogin() {
    var phone = result.get("🟢账号:");
    var pwd = result.get("🔴密码:");
    if (phone != "" && pwd != "") {
        let login_url =
            "https://www.gongzicp.com/cp.UserExternal/account_login";
        let password = AESencrypt(pwd);
        let m = { account: phone, password: password, nationCode: 86 };
        let body = Da(toBinary(m), "text");
        let ck = "_c_n_=" + java.md5Encode(Bc())
        let headers = {
            "User-Agent":
                "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/129.0.0.0 Safari/537.36",
            "content-type": "application/grpc-web-text",
            "Accept": "application/grpc-web-text",
            "cookie" :String(ck)
        };
        let n = java.post(login_url, body, headers).body();
        if (n.match(/AAAAA/)) {
            let t = n.split("gAAAA")[0];
            let b = java.base64Decode(t);
            let token = b.slice(11, 43);
            let refresh_token = b.slice(45, 78);
            save_oken(ck,token,refresh_token)
            source.setVariable(ck);
            java.longToast("✅登录成功!");
        }else{
            java.longToast("❌️登录失败");
        }
    } else {
        java.longToast("❌️账号或密码不能为空");
    }
}

function save_oken(cookie,token,refresh_token) {
    let zz = result.get("是否开启作者有话说，开启填1其他为不开启");
    let yc = result.get("是否开启正文延迟(10s)，开启填1其他为不开启");
    java.log("yc:"+yc)
    zz = zz == "" || zz == null ? "0" : zz;
    yc = yc == "" || yc == null ? "0" : yc;
    let timestamp = new Date().getTime();
    let times = java.timeFormat(timestamp);
    option = {
        token: String(token),
        refresh_token:String(refresh_token),
        zz: String(zz),
        ck: String(cookie),
        yc: String(yc),
        times: times
    };
    source.putLoginHeader("&" + JSON.stringify(option));
    java.longToast("✅保存token：" + token);
}

function login() {
    java.longToast("登录完成");
}

