ruleContent.payAction:
cid = baseUrl.match(/chapterId=(\d+)/)[1];
nid = baseUrl.match(/novelId=(\d+)/)[1];
Map = source.getLoginHeaderMap();
Map = Map==null?{}:Map;
token = Map["token"];

enbody = String(Date.now())+":"+token+":"+nid;
sign = java.createSymmetricCrypto("DES/CBC/PKCS5Padding", String("KW8Dvm2N"), String("1ae2c94b")).encryptBase64(enbody)

body = "versionCode=287&novelId="+nid+"&chapters="+cid+"&orderType=15&isautoBuy=0&autosubscribeStatus=0&notUseBackpoint=&sign="+sign;

url = "https://android.jjwxc.net/androidapi/buy,"
post = {
	"method":"POST",
	"body":String(body)
	}
 a = java.ajax(url+JSON.stringify(post));

 if(/购买成功/.test(JSON.parse(a).message)){
 	java.longToast(JSON.parse(a).message+"\n请刷新正文");
 	}else{
 		mm = JSON.parse(a).message==""?JSON.parse(a).popupdata.popupmessage+"\n❗️进晋江app进行一次验证后❗️才能在阅读app使用购买功能":JSON.parse(a).message;

 		java.longToast(mm);
 		}

ruleContent.replaceRegex:
@js:
//去除章节简介与正文重复
if(/^◎/.test(result) ){
chapter = result.match(/◎([\s\S]+?)(…)*◎/)[1].replace(/\s/g,'').replace(/,/g,'，');

chapter2=result.match(/◎[\s\S]+?◎([\s\S]+)/)[1].replace(/\s/g,'').replace(/,/g,'，');

//前4个字相同就删除
num = 4
if(chapter.substring(0,num)==chapter2.substring(0,num)){
	result = result.replace(/◎[\s\S]+?◎/,'')
	}else{result}
}else{result}

