u = source.bookSourceComment.match(/网址〖([^〗]*)〗/)[1];
sort = [];
push = (title, url, type) => sort.push({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: type
  }
});
X = org.jsoup.Jsoup.parse(java.ajax(u)).select(".SHsectionTwo-top .SHsectionTwo-bottom-content li a[href*=/s/]");
