@js:
try{
var r = result, n = 0;
Array.from(r, (x, i) => {
	var name = x.text(),
	link = x.attr("href");
	dir = {text: name, href: link,
	toString: function(){
		return JSON.stringify(this)}
	};
	if(/javascript/i.test(x) ){
	  let add = r[i + 1], sub = r[i - 1],
	  count = 0, max = 20;
	  //加减以及计数次数
	  var url = null,
	  na = _ => r[i + _],
	  ns = _ => r[i - _];
	  let asurl = au => 
	  /^https?/.test(au) ? 
		au : book.origin + au;
		$ = (r,c) => java.getString(r,c);
		tips = n => {
		  msg = "目录缺失，尝试修复中，稍等！";
		  n += 1; n < 2 && java.log(msg);
		 return //java.toast(msg)
		 }
		 if(/\d+(\.html?)?$/.test(add||sub)){
		   if(na(1)&&na(2)){
		   	//网站禁了head请求，取消判定
		     u1 = na(1).attr("href"),
		     u2 = na(2).attr("href");
		     m = Number(u1.match(/(\d+).html?/)[1]) - Number(u2.match(/(\d+).html?/)[1]);//计算差数
		     aurl = asurl(u1);
		     do{
		     	try{
		       code = java.head(aurl,{Referer:baseUrl}).statusCode();
		       }catch(e1){code = 404}
		       count++
		       n += m;
		       if(n >= 1){
		         url = String(url).replace(/\d+(.html?)/,`${n}$1`);
		       }
		       if(count > max){
		         java.log("已到最大次数！")
		         break;
		       }
		     }while(code >= 404 && count <= max);
		   }else if(ns(2)&&ns(1)){
		     u2 = ns(2).attr("href"),
		     u1 = ns(1).attr("href");
		     m = Number(u2.match(/(\d+).html?/)[1]) - Number(u1.match(/(\d+).html?/)[1]);//计算差数
		     surl = asurl(u1);
		     do{
		     	try{
		       code = java.head(surl,{Referer:baseUrl}).statusCode();
		       }catch(e2){code=404}
		       count++
		       n += m;
		       if(n >= 1){
		         url = String(url).replace(/\d+(.html?)/,`${n}$1`);
		       }
		       if(count > max){
		         java.log("已到最大次数！")
		         break;
		       }
		     }while(code >= 404 && count <= max);
		   }
		   dir.href = url;
		 }
		 if(!url && add){
	    tips(n);
			var ah = add.attr("href"), 
			aurl = asurl(ah);
			h = java.ajax(aurl);
			path = "text.上一@href";
			u = $(path, h);
			dir.href = u;
			if(u != "") url = u;
			//java.log(url)
		}
		if(!url && sub){
		  tips(n);
			var ahs = sub.attr("href"), 
			surl = asurl(ahs);
			do{
			  h = java.ajax(surl),
			  path = "text.下一@href";
			  surl = asurl($(path, h));
			}while(/_\d+/.test(surl));
			dir.href = surl;
		}
		//java.log(dir.toString())
	}
	return dir;
	});
}catch(err){
	java.log("目录修复出错！\n"+err)
	result
	}

