			f = 1
			if (m&&/k/i.test(m)) {
				f = 1000
			}else if(m&&/m/i.test(m)) {
				f = 1000000
			}
			value = Number(data.match(/[\d.]+/))*f
			break;
		default:
			value = data
	}
	return value
}
