@js:
var list = [];
var  a = baseUrl.match(/(\d+)\/(\d+)\/(\d+)/);
//java.log(a);
var ml = result.match(/\/down\/\d+\/\d+\/\d+\//g).length;
//java.log(ml);
var  href = "http://www.hcxsw3.com/down/";

if (a && a.length > 0) {
  for(let i = 2; i < ml; i++){
    var g = href + a[1] + "/" + a[2] + "/" + i + "/";
    list.push(g);
  }
} else {
  // 处理 baseUrl 未匹配到的情况
  java.log("baseUrl 没有😭");
}

if (ml === 0) {
  // 处理 result 未匹配到的情况
  java.log("result 没有😭");
}

result = list;

