@js:
list=[]
$=result
for(i in $){
if(String($[i]).match(/class="fj/)){
	t=String($[i].text()).replace(new RegExp(book.name,''),'').replace(/（.*|\(.*/g,'');
		list.push({
				text: "📖【"+t+"】📖",
				volume: true
			})
	}
else{
		$[i].select("a").forEach(a=>{
				list.push({
						text: a.text(),
						href: a.attr("href")
					})
			})
	}
}
