<js>
result.toArray().slice(0, 3).map(el => {
	var url = String(el.select('a:eq(1)').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
	var J = org.jsoup.Jsoup.parse(java.ajax(url));
	var o = selector => String(J.select(selector).text()).replace(/.*：/,'');	
	return {
		name: o('h2'),
	  author: o('.block_txt2 p:eq(3)'),
	  kind: o('.block_txt2 p:eq(4)') + ',' + o('.block_txt2 p:eq(5)').replace(/中|已/,''),
	  latest: o('.block_txt2 p:eq(7)').replace(/^(正文|VIP章节|最新章节)?(\s+|_)|[\(（【].*[求更谢乐发推].*/g, '') + ' • ' + o('.block_txt2 p:eq(6)'),
	  intro:  o('.intro_info').replace(/\d+/, ''),
	  cover: J.select('.block_img2 img').attr('src'),
		url: url,
	};
}); 
</js>

