var v=source.getVariable();
var su=source.getKey();
var url=(v==null||v=="")?su:v;
url+="/mulu/";
var html=java.ajax(url);
var h=org.jsoup.Jsoup.parse(html)
var a=h.select('ul[class=clearfix] li a');
var list=[];
for(i in a){
	var text=a[i].text();
	var href=a[i].attr("href");
href=String(href).replace("1.html",'{{page}}.html');
	var add=text+"::"+href;
	list.push(add);
	}
list.join("&&")

