searchUrl:
@js:
var javaImport = new JavaImporter();
javaImport.importPackage(
    Packages.java.lang,
    Packages.org.jsoup,
    Packages.java.util,
    Packages.java.net,
);

with(javaImport){
    function search(url,key){
        let map = HashMap()
        map.put("Content-Type","application/x-www-form-urlencoded");
        var execute = Jsoup.connect(url)
                .ignoreContentType(true)
                .followRedirects(false)
                .headers(map)
                .method(Connection.Method.POST)
                .requestBody("keyboard="+encodeURI(key)+"&show=title,writer,ftitle,keyboard&tempid=1&tbname=article")
                .execute();
        return execute.header("Location");
    }
}

