@js:
(() => {
    if (!result || result.trim().length === 0) {
        const idMatch = baseUrl.match(/\/(\d+)\/?$/);
        if (!idMatch) {
            java.toast("错误: 书籍ID提取失败");
            return "";
        }

        const id = idMatch[1];
        if (id.length < 3) {
            java.toast("错误: 书籍ID长度不足3位");
            return "";
        }

        const prefix = id.substring(0, 3);
        return `http://www.yeudusk.com/html/${prefix}/${id}`;
    } else {
        return result;
    }
})();

ruleToc.chapterList:
@css:
.chapters > ul > li,
div.index > ul.chapters > li,
.chapters > div.volume,
.index > div.chapters > div.volume
@js:
list = [];
list.push({
    text: "💮↓ 试读卷 ↓💮",
    volume: true
});
$ = result;
for (i in $) {
    if (/volume/.test(String($[i]))) {
        text = "🏮↓〖" + String($[i].text()) + "〗↓🏮";
        list.push({
            text: text,
            volume: true
        });
    } else {  
        $[i].select("li").forEach(a => {
            text = a.select("a").text();
            info = String(a.select("a").attr("title")).replace(/更新，共/g, '');
            href = a.select("a").attr("href");
            list.push({   
                text: text,
                info: info,
                href: href
            });
        });
    }
}
list

