eval(String(source.loginUrl));
var timestamp = new Date().getTime();
var uptime = cache.get("uptime");
if (!uptime) {
    update("发现");
    cache.put("uptime", (timestamp + 21600000).toString());
} else if (timestamp >= Number(uptime)) {
    update("发现");
    cache.delete("uptime");
    cache.put("uptime", (timestamp + 21600000).toString());
}
let Cookie = cookie.getCookie(GetUL());
if (!/lf_user_auth/.test(Cookie) && source.getLoginHeader()) {
    let ck = source.getLoginHeaderMap()['Cookie']
    cookie.setCookie(GetUL(), ck);
    java.toast('登录状态掉了，已重新登陆');
}
var result = [];
var push = (title, url, size) =>
    result.push({
        title: title,
        url: url ? `{{GetUL()}}${url}` : url,
        style: {
            layout_flexGrow: 1,
            layout_flexBasisPercent: size
        }
    });
push(`当前站源：${GetUL()}`, null, 1)
if (source.getLoginHeader()) {
    let jc = source.getLoginHeaderMap();
    let inf = decodeURIComponent(jc.get("Cookie"));
    let info = decodeURIComponent(inf).match(/\{[\s\S]+\}/)[0];
    let username = JSON.parse(info)['username'];
    let expe = java.ajax(`${GetUL()}/user/index/index.html`);
    let ex = org.jsoup.Jsoup.parse(expe).select('.group , .app_accountBlock_lis ul li');
    push(`用户◎${username}◎中心`, null, 1);
    push("书架", "/user/bookshelf?page={{page}}", 0.45);
    push("阅读记录", "/user/recentread/index.html", 0.45);
    ex.forEach(xp => {
        nam = String(xp.text()).replace(/[^\u4e00-\u9fa5a-zA-Z0-9]/g, "");
        title = /\d/.test(nam) ? nam.replace(/\d+/, "：$&") : nam.replace(/^/, "用户等级：");
        push(title, null, 0.45);
    });
} else {
    push("未登录", null, 1);
}
push("--------书库--------", null, 1);
upsort = Sort_() ? `/all{{Sort_()}}.html?page={{page}}` : `/all/order/update_time+desc.html?page={{page}}`;
push("最近更新", upsort, 0.45);
push("原创", "/original.html?page={{page}}", 0.45);
push("排行", null, 1);
var src = java.ajax(`${GetUL()}/all/order/hits+desc.html`);
var ph = org.jsoup.Jsoup.parse(src).select('.paihang_ds a');
ph.forEach(ta => {
    nam = ta.text();
    ul = ta.attr('href')
    push(nam, ul, 0.45);
})
push("分类", null, 1);
var tag = org.jsoup.Jsoup.parse(src).select('.fenlei_ds a');
tag.forEach(ta => {
    nam = ta.text();
    ul = String(ta.attr('href')).replace(/$/, "?page={{page}}").replace(/lists/, "all/id").replace(/\.html/, `{{Sort_()}}$&`);
    push(nam, ul, 0.25);
})
JSON.stringify(result);

