//自动处理验证
function content() {
    let text;
    let html = java.ajax(chapterul);
    if (html.includes("滑动验证")) {
        let headers = {
            "User-Agent": "Mozilla/5.0 (Linux; Android 9) Mobile Safari/537.36",
            "Referer": chapterul
        };
        try {
            let s = html.match(/src=\"(\/huadong[^"]+)/)[1];
            let sr = java.get(url + s, headers).body()
            eval(sr.match(/(key=\".+?,value=\"[^;]+?\;)/)[1]);
            let va = getval(value);
            let codeul = url + sr.match(/([^"]+yanzheng_huadong\.php[^"]+)/)[1] + `${key}&value=${va}`;
            let ck = java.get(codeul, headers);
            java.log("验证Cookie：" + JSON.stringify(ck.cookies()));
            java.toast("检测到验证,尝试自动处理……");
            let tx = java.ajax(chapterul);
            text = java.getString('#chapterContent@html', tx);
        } catch(error) {
            java.log(error);
            text = "自动验证失败，请重试!";
        }
    } else {
        text = java.getString('#chapterContent@html', html);
    }
    return text;
}

let text;
if ( Math.abs(chapter.index - book.durChapterIndex) <= 3) {
    text = content();
} else {
	   text = "因网站速率限制，当前预下载超过三章，为避免被封ip，被动停止解析正文。\n建议调小预下载，保持正常阅读速度，刷新即可重新获取";
}

//处理重新获取文本时可能会出现的错误。
let retryCount = 0;
const maxRetries = 3;
let result;
while (retryCount < maxRetries) {
    if (text != false && text !== "") {
        result = text;
        break;
    } else {
    	    retryCount++;
        java.toast(`未获取到文本，正在重试...（当前第${retryCount}次重试）`);
        text = content();
    }
}
if (retryCount === maxRetries) {
    result = "重试三次仍未获取到文本，请检查网络状况。"
}
result;

