function register(is)
{
    if(is == undefined) return true;
    info = result
    so = getUrl();
    pw = info.密码
    imgurl = so + 'checkcode.php?rand=' + Math.random();
    if(is)
    {
        info["用户名"] = `8x${parseInt(Math.random() * 1000000000000)}`;
        info["密码"] = "123456789";
        info["邮箱"] = `${parseInt(Math.random() * 1000000000000)}@163.com`;
        info["QQ"] = "";
    }
    url = so + (is ? "register.php" : "login.php");
    body = `username=${info["用户名"]}&password=${info["密码"]}&repassword=${info["密码"]}&email=${info["邮箱"]}&sex=0&qq=${info["QQ"]}&act=${is? "newuser" : "login"}&submit=&ajax_request=${Date.now()}&checkcode=${java.getVerificationCode(imgurl)}`;
    result = java.post(url, body,
    {
        "Content-Type": "application/x-www-form-urlencoded"
    });
    cookie = result.cookies();
    ck = cookie ? String(cookie).match(/\{(.*?)\}/)[1].replace(/,/g, ';') : "";
    if(!ck)
    {
        java.toast("注册失败，重新注册！");
        return false;
    }
    source.putLoginHeader(JSON.stringify(
    {
        "Cookie": ck,
        "User-Agent": "Mozilla/5.0",
        "Referer": so
    }));
    source.putLoginInfo(JSON.stringify(
    {
        "用户名": info["用户名"],
        "密码": info["密码"],
        "邮箱": info["邮箱"]
    }));
    //java.log(ck)
    //java.log(result.body())
    java.log(url + "," + JSON.stringify(body))
    java.toast(`注册成功----${info["用户名"]}`);
    return true;
}

function login(b)
{
    if(b == undefined)
    {
        let x = source.getLoginInfoMap().get("页数")
        v = String(source.getVariable()).split(",");
        v[0] = x
        source.setVariable(v.join(","));
        return true;
    }
    info = result
    so = getUrl();
    pw = info.密码
    imgurl = so + 'checkcode.php?rand=' + Math.random();
    if(b)
    {
        url = so + 'login.php,'
        body = `username=${info.用户名}&password=${pw}&usecookie=1&act=login&jumpreferer=1&t_btnlogin=&ajax_request=${Date.now()}&checkcode=${java.getVerificationCode(imgurl)}`
    }
    else
    {
        url = so + 'register.php,'
        body = `username=${info.用户名}&password=${pw}&repassword=${pw}&email=${info.邮箱}&sex=0&qq=${info.get('QQ(非必填)')}&act=newuser&Submit=&do=submit&ajax_request=${Date.now()}&checkcode=${java.getVerificationCode(imgurl)}`
    }
    post = JSON.stringify(
    {
        "body": String(body),
        "method": "POST"
    })
    url += post
    result = java.ajax(url)
    //java.log(result)
    if(result.match(/已注册/))
    {
        java.toast('\n' + '❗用户名已注册❗')
    }
    else if(result.match(/Email不能为空/))
    {
        java.toast('\n' + '❗Email不能为空❗')
    }
    else if(result.match(/该用户不存在/))
    {
        java.toast('\n' + '❗用户不存在❗')
    }
    else if(result.match(/已经有人登陆/))
    {
        java.toast('\n' + '❗该帐号已经有人登陆❗')
    }
    else if(result.match(/暂停新用户/))
    {
        java.toast('\n' + '❗对不起，本站暂停新用户注册❗')
    }
    else if(result.match(/密码错误/))
    {
        java.toast('\n' + '❗密码错误❗')
    }
    else
    {
        java.log(url)
        java.toast(info["用户名"] + ' 登录成功');
        c = cookie.getCookie(getUrl())
        source.putLoginHeader(c);
    }
}
