function wn(t) {
                return "object" !== typeof t || null === t ? t :
                    Array.isArray(t) ? t.map(wn) : Object.keys(t)
                    .sort().reduce(((e, n) => (e[n] = wn(t[n]),
                    e)), {})
}

function mn(u,i,b) {
	 const { java , source } = this
  const n = Date.now();
  const base64String = java.base64Encode(n + u);
  const r = java.md5Encode(base64String);
	 const e = source.getLoginHeader() ? source.getLoginHeaderMap().get("authorization") : "";
	 const l = java.toURL(u).searchParams ? wn(JSON.parse(JSON.stringify(java.toURL(u).searchParams))) : {};
 const s = java.toURL(u).pathname;
 const g = b ? wn(b) : {};
 const h = [i,s,JSON.stringify(l),JSON.stringify(g),n,r].join("|");
 //java.log(h)
 let key = java.HMacHex(h, "HmacSHA256", e);
 let authorization = source.getLoginHeaderMap()?.get("authorization") + ";" ?? "";
 return `${authorization}${key};${n};${r}`
}

function formatDate(dateStr) {
  const date = new Date(dateStr);
  const pad = n => String(n).padStart(2, '0');
  return `${date.getFullYear()}-${pad(date.getMonth()+1)}-${pad(date.getDate())} ` +
         `${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;
}

