java.toast("🔥正在刷新列表,请稍等！");
var push = (title, url, size) => result.push({
  "title": title,
  "url": url,
  "style": {
    "layout_flexGrow": 1,
    "layout_flexBasisPercent": size
  }
});

var fl = java.ajax("http://www.biqugeus.com");

var ph = java.ajax("http://www.biqugeus.com/novel/");

if (fl && ph) {

  a = org.jsoup.Jsoup.parse(fl).select(".nav a")

  b = org.jsoup.Jsoup.parse(ph).select(".toplist a")

push("🔥分类小说🔥",null,1)
    for(var i=0;i<a.length;i++){
        title=a[i].text()
        url=a[i].attr("href")
        size=0.33;
        title = String(title).replace(/\s/g, "");
       if (title === "首页"||title==="排行榜单") {
            continue; 
        }
        url=baseUrl+String(url).replace(/\d+\/$/,"")+"{{page}}.html";
        push(title,url,size)
    };

push("🔥排行榜单🔥",null,1)
    for(var i=0;i<b.length;i++){
        title=b[i].text()
        url=b[i].attr("href")
        size=0.33;
        title = String(title).replace(/\s/g, "");
        url=baseUrl+String(url).replace(/\d+\.html$/,"");
        push(title,url,size)
    };
} else {

  java.toast("🤔列表刷新失败！！！");
}
JSON.stringify(result);

