function randomUa(){
	let r = (a,b) => Math.floor(Math.random()*(b-a)+a), x = l =>{for(i=0,s='';i<l;i++){s+=r(0,10)}return s}, v = r(500, 700)+'.'+x(2)
	return `Mozilla/5.0 (Linux; Android ${r(5,15)}; wv) AppleWebKit/${v} (KHTML, like Gecko) Chrome/${r(76,122)}.0.${x(4)}.${x(2)} Mobile Safari/${v}`
}
function getDomain(){
	const {source} = this
	let domain = v.call(this, 'Domain')
	if(!domain||domain=='')domain=source.key
	return domain
}
function completeUrl(url, base) {
  let baseUrl = base || getDomain.call(this)
  if(v.call(this,'k'))url = String(url).replace(/.*\/\/[^/]+/, '')
  let m = baseUrl.match(/(.+?)\/\/([^/]+)(.*\/?)(.*)/)
  let fill = ''
  if (url.match(/^https?:/)) {
  } else if (url.match(/^\/\//)) {
    fill = m[1]
  } else {
    fill = m[1] + '//' + m[2]
    if (!url.match(/^\//)){
