@js:
sort=[];
push=(title,url,type1,type2)=>sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: type1,
				layout_flexBasisPercent: type2
			}
	});
push("全部🌊分类",null,1,1)
  nrList=[["🌊全本🌊","/quanben"],["🌊连载🌊",""]]
	arList=[["高辣","1"],["百合","2"],["言情","3"],["情欲","4"],["耽美","5"],["海棠","6"],["其他","8"]]
	 nrList.map(([teg,ure])=>{
	 	push(teg,`${ure}/sort/{{page}}/`,1,1);
	arList.map(([tag,uri])=>{
		url=`${ure}/sort/${uri}/{{page}}/`;
		push(tag, url, 1, 0.25);
		});
		});
//java.log(JSON.stringify(sort))
JSON.stringify(sort)

