function Login() {
let rkey = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789-._~';
let cv='';
for (let i = 0; i < 128; i++) {
	let ra = Math.floor(Math.random()*rkey.length);
	cv+=rkey.charAt(ra);
	}

let aly = new JavaImporter(
Packages.cn.hutool.crypto.digest.Digester,
Packages.cn.hutool.core.codec.Base64,
Packages.cn.hutool.crypto.digest.DigestAlgorithm);
with (aly) {
	//转ASCII数组
	let asBs =  Packages.java.lang.String(cv).getBytes("US-ASCII");
	//创建SHA256并计算
	let digester = new Digester(DigestAlgorithm.SHA256);
	let hashBs = digester.digest(asBs);
	//Base64URL编码并移除填充
	var codeChallenge  = Base64.encodeUrlSafe(hashBs);
}

let urllg =`https://app-api.pixiv.net/web/v1/login?code_challenge=${codeChallenge}&code_challenge_method=S256&client=pixiv-android`;
source.removeLoginHeader();
cookie.removeCookie('https://accounts.pixiv.net');
cookie.removeCookie('https://accounts.google.com');
cookie.removeCookie('https://api.weibo.com');
