@js:
function createSearchUrl(key) {
  return `https://www.sososhu.com?q=${java.encodeURI(key, 'utf-8')}&site=dangyuedu&Submit=${java.encodeURI('ÊêúÁ¥¢', 'utf-8')}`;
}

function createHeaders(origin, referer) {
  return {
    'Accept-Encoding': 'gzip, deflate, br, zstd',
    'Content-Type': 'application/x-www-form-urlencoded',
    'origin': origin,
    'x-requested-with': 'mark.via',
    'referer': referer
  };
}

function handleDirectResult(ht1, key) {
  let che = `‰∏é‚Äú${key}‚ÄùÁõ∏ÂÖ≥ÁöÑÂ∞èËØ¥`;
  if (ht1.body().includes(che)) {
    java.put('sht', ht1.body());
    return true;
  }
  return false;
}

function handleRedirect(u1, hd) {
  hd.Accept = 'text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,/;q=0.8,application/signed-exchange;v=b3;q=0.7';
  u1 = `https://www.sososhu.com${u1}`;
  let u2 = java.get(u1, hd).header('location');
  java.log(u2);
  let sht = java.get(u2, hd);
  java.put('sht', sht.body());
}

function calculateSum(o, nonce) {
  let d = 0;
  for (let i = 0; i < o.length; i++) {
    let r = o[i];
    if (/^[a-zA-Z0-9]$/.test(r)) {
      d += o.charCodeAt(i) * (nonce + i);
    }
  }
  return d;
}

function handleDDoSProtection(ht1, su, hd) {
  let ck1 = ht1.header('set-cookie').split(';')[0];
  let o = String(ck1.split('=')[1]);
  java.log(o);

  let nonce = ht1.body().match(/var nonce = (\d+)/);
  nonce = nonce ? +nonce[1] : null;
  java.log(nonce);

  let d = calculateSum(o, nonce);
  java.log(String(d));

  let body = `sum=${d}&nonce=${nonce}`;
  hd['x-ge-ua-step'] = 'prev';
  hd.cookie = ck1;

  let ht2 = java.post(su, body, hd);
  java.log(ht2.body());

  let ck2 = ht2.header('set-cookie').split(';')[0];
  java.log(ck2);

  Packages.java.lang.Thread.sleep(5000);

  delete hd['x-ge-ua-step'];
  hd.Accept = 'text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,/;q=0.8,application/signed-exchange;v=b3;q=0.7';
  hd.cookie = ck2;

  let sht = java.get(su, hd);
  let u1 = sht.header('location');
  java.log(u1);

  if (u1) {
    handleRedirect(u1, hd);
  } else {
    java.put('sht', sht.body());
  }
}

function main(key) {
  java.put('key', key);
  let su = createSearchUrl(key);
  let ht1 = java.get(su, {});
  let hd = createHeaders('https://www.sososhu.com', su.replace('com', 'com/'));
  let u1 = ht1.header('location');

  if (handleDirectResult(ht1, key)) {
    return;
  } else if (u1) {
    handleRedirect(u1, hd);
  } else {
    handleDDoSProtection(ht1, su, hd);
  }
}

main(key);
