@js:
sort=[];
push=(title,url,type1,type2)=>sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: type1,
				layout_flexBasisPercent: type2
			}
	});
push("全部🌊分类",null,1,1)
	arList=[["校园","61"],["贤者小说","79"],["玄幻","62"],["奇幻","75"],["强奸","74"],["同人","73"],["科幻","71"],["乡村","63"],["都市","64"],["系统","69"],["武侠","68"],["历史","67"],["明星","72"],["乱伦","65"],["穿越","70"],["凌辱","46"],["反差","22"],["堕落","18"],["纯爱","19"],["伪娘","52"],["性奴","45"],["萝莉","48"],["熟女","56"],["禁忌","51"],["NTR","54"],["媚黑","53"],["绿帽","55"],["调教","58"],["女主","59"],["正太","50"],["下克上","43"],["反转","23"],["羞耻","34"],["百合","47"],["重口","21"],["其他","57"]]
	arList.map([title,uri]=>{
		url=`{{getUrl()}}lists/${uri}.html<,?page={{page+1}}>`;
		push(title, url, 1, 0.25);
		});
		push("最近🌊更新",`{{getUrl()}}all/order/update_time+desc.html<,?page={{page+1}}>`,1,1);
			push("🌊排行🌊",null,1,1)
	xrList=[["总排行","hits"],["本日排行","hits_day"],["本周排行","hits_week"],["本月排行","hits_month"]]
	xrList.map([title,uri]=>{
		url=`{{getUrl()}}other/rank_hits/order/${uri}.html`;
		push(title, url, 1, 0.25);
		});

//java.log(JSON.stringify(sort))
JSON.stringify(sort)

