let fbyurl="https://wnlink.ru/";

function fabuye() {
	let webr = java.startBrowserAwait(fbyurl,'发布页',false).body();
	let rr = org.jsoup.Jsoup.parse(webr);
	rr = rr.select('.content-top li:gt(1):lt(4) a');
	let re=[];
for (let char of rr) {re.push(char.attr('href'));}
source.put('yurl',re);
cesu();
}

function cesu() {
	['　　　','·　　','··　','···'].forEach((_,i) => {java.toast('\n\n测试中'+_+'\n');Packages.java.lang.Thread.sleep((3-i)*100);});
let url = (source.get('yurl')+[]||surl()).split(',');
let tm=[], mit=99, miu='';
	try {
	for (char of url) {
  let startTime=new Date().getTime();
	//java.head(char,{	'User-Agent': u_a});
	java.ajax(char);
	let endTime=new Date().getTime();
	let speed = (endTime - startTime) / 1000;
	speed = speed.toFixed(2);
	if (mit>speed) {mit=speed; miu=char;}
	tm.push(speed);
	}
	java.longToast('\n'+url[0]+'：'+tm[0]+'秒\n'+url[1]+'：'+tm[1]+'秒');
	} catch(err) {java.longToast(err);}
	let Info=source.getLoginInfoMap()||{};
	Info['地址'] = miu;
	cache.putMemory('ssmhinfo',Info);
source.putLoginInfo(JSON.stringify(Info));
}

