            cover = `https://i9-static.jjwxc.net/novelimage.php?novelid=${id}`;
        }

        let header = {
            headers: {
                referer: cover.match(/(^https?:\/\/.*?\/)/)[1]
            }
        };
        cover = `${cover},${JSON.stringify(header)}`.replace(/wx\d+/, 'wx2');

        let cat = String(J.select('a[style="text-decoration:none;color: red;"]').text()).replace(/\s/g, ',');
        let status = String(J.select('span[itemprop="updataStatus"]').text());

        let size = String(J.select('span[itemprop="wordCount"]').text()).replace(/字/, '');
        let n = String(java.getElement('@@id.oneboolt@tag.tr.-2@span').text())
            .replace(/\n/, '•')
            .replace(/\*最新更新/, '')
            .replace(/(.*?)\[(?:VIP|福利番外)\]/, '🔒$1');
        let num = java.getElement('@@id.oneboolt@tag.tr.-2@tag.td.0').text();
        let genre = J.select('[itemprop="genre"]').text().split('-')[1];
        let view = html.match(/<span>作品视角：<\/span>([^<]+)<\/li>/)[1].replace(/\s/g, '');

        json.push({
            title,
            author,
            cat: `${view},${status},${genre},${cat}`,
            size,
            url: `https://app-cdn.jjwxc.net/androidapi/novelbasicinfo?novelId=${id}`,
            des,
            new: `${num}、${n}`.replace(/(\d+、)🔒/, '🔒$1').replace(/^章节、|、$/, ''),
            cover
        });
    } catch (e) {
        java.log("搜索列表的错误："+e);
    }
}
JSON.stringify(json)
</js>
