ruleToc.preUpdateJs:
var tocUrl = bDe(book.tocUrl);
var token = getToken(tocUrl);
var novelId = getNid(tocUrl);

try{
	var newToken = Map("token");
	if(!/^\d+_[\w\d]{16,}/.test(newToken) && newToken!=""){
		setHead("token","");
		java.refreshTocUrl();
		java.longToast("token不正确，已清除token值，请重新登录");
	}

	var oldToken = token;
	if(newToken != oldToken){
	    token = newToken
		}

if(/^\d+_[\w\d]{16,}/.test(newToken)){
	 tocUrl = "https://android.jjwxc.net/androidapi/chapterList?novelId="+novelId+"&more=0&whole=1&token="+token;

   let src = java.ajax(tocUrl);
   cache.put("tokenSrc"+novelId, src, 60)

	 let message = JSON.parse(src).message;
	 if(/登入验证失败/.test(message)){
	 	        setHead("token","");  	  	      
	            java.longToast("登录token已失效，已清除token值，请重新登录");
	            java.refreshTocUrl();  
	    }else if(newToken != oldToken){
	       // 	java.longToast("目录链接更改为新token");
	        	java.refreshTocUrl();
	    }else{
			    	cache.put("tokenSrc"+novelId, src, 60)
		}  
	}else if(/token=&/.test(tocUrl)){
			  java.refreshTocUrl();
			 // java.longToast("更新目录链接的token");
			}  
	}catch(e){
	java.log("这是错误："+e)
	}

