javaImport.importPackage(
	  //Packages.android.util,
    Packages.java.lang,
    Packages.javax.crypto.spec,
    Packages.javax.crypto,
    Packages.java.util,
    Packages.java.io,
    Packages.java.security, 
);
with(javaImport) {
    var px2b="F1d36851BC0e5943042b261dFcFEd0e5";
    var sto = "5fFf6D94079904826ab080B8179E9376";
  function stt(bArr) {
        if (bArr == null) {
            return null;
        }
        var stringBuffer = new StringBuffer();
        for (i=0;i< bArr.length;++i) {
            var hexString = Integer.toHexString(bArr[i] & 255);
            if (hexString.length() == 1) {
                hexString = '0' + hexString;
            }
            stringBuffer.append(hexString);
        }
        return stringBuffer.toString();
    };
    function thirdtoken(str) {
       var h= "{\"time\":" + Math.round(new Date()) + "}";

       var bytes = String(str).getBytes();
       var bytes2 = String(px2b).getBytes();
       var bytes3 = String(h).getBytes();

       var keyString = "0123456789ab";
       var bArr = String(keyString).getBytes();    
       new SecureRandom().nextBytes(bArr); 
       //java.log(bArr.length)      
       var secretKeySpec = new SecretKeySpec(bytes, "AES");
       var instance = Cipher.getInstance("AES/GCM/NoPadding");
       instance.init(1, secretKeySpec, new GCMParameterSpec(128, bArr));
       instance.updateAAD(bytes2);
       var doFinal = instance.doFinal(bytes3);
       if (doFinal == null) {
                return null;
        }
        var i = stt(bArr);
        var i2 = stt(doFinal);
        return px2b + ':' + i + i2;
    };

    function gy2b(t,s,e) {
    	return Arrays.copyOfRange(t,s,e)
	}
    function contentDecode(str, str2) {
        var decode = [];
        var str3 = "";
        var decode = android.util.Base64.decode(String(str).getBytes(), 0);
        if ( decode == null) {return null;}     
        var secretKeySpec = new SecretKeySpec(decode, 0, decode.length, "AES");
        var decode2 = android.util.Base64.decode(str2, 0);
        var b = gy2b(decode2, 0, 16);
        var b2 = gy2b(decode2, 16, decode2.length);     
        var ivParameterSpec = new IvParameterSpec(b);
        var cipher = Cipher.getInstance("AES/CBC/PKCS5Padding");
        cipher.init(2, secretKeySpec, ivParameterSpec);
        var str4 = new String(cipher.doFinal(b2));
        return str4;
    }
}

