function decryptParams(encryptedParams) {
  const parsedData = CryptoJS.enc.Base64.parse(encryptedParams);
  const iv = CryptoJS.lib.WordArray.create(parsedData.words.slice(0, 4)); 
  const encryptedWords = parsedData.words.slice(4);  
  const encryptedHex = CryptoJS.enc.Hex.stringify(CryptoJS.lib.WordArray.create(encryptedWords));
  const key = CryptoJS.enc.Utf8.parse('9S8$vJnU2ANeSRoF'); 
  const decrypted = CryptoJS.AES.decrypt({
    ciphertext: CryptoJS.enc.Hex.parse(encryptedHex)
  }, key, { iv: iv });
 const decryptedText = decrypted.toString(CryptoJS.enc.Utf8);
  const decryptedJson = JSON.parse(decryptedText);
  return decryptedJson.images;
}
img = String(decryptParams(params)).replace(/\,/g,'\n');
img.split('\n').map(x=>`<img src="${x}">`).join('\n');

