ruleToc.chapterList:
class.BCsectionTwo-top-chapter
@js:(function(){
	// data属性配置项
    var orderAttrs = ['data-x', 'data-nmb', 'data-tmd','data-ido'];

    //排序方式判断
    var originalOrder;
    try {
        originalOrder = JSON.parse(src.match(/originalOrder\s*=\s*([.+?])/)[1]);
    } catch(e) { java.log("⚠️ originalOrder 解析失败"); }

//开始排序
    result.sort(function(a, b){
        if(originalOrder) {
            return originalOrder[result.indexOf(a)] - originalOrder[result.indexOf(b)];
        }
        for(var attr of orderAttrs) {
            let va = parseInt(a.attr(attr));
            let vb = parseInt(b.attr(attr));
            if (!isNaN(va) && !isNaN(vb)) return va - vb;
        }
        return 0;
    });

//输出结果
    return result.stream().map(e => e.select("a").first()).filter(e => e != null).toList();
})()

