			   java.toast(msg);
			}
		} else {
			let getCodeurl = getloginUrl() + "&checktype=" + t + "&checkdevicecode=" + 验证码;
			let data = java.ajax(getCodeurl);
			let token = JSON.parse(data).token ?? JSON.parse(data).message;
			java.longToast(token);
			java.log("获取到的token值: " + token)
			LoginToken(token)
		}
	} catch (e) {
		java.log(e)
	}
}

function LoginToken(token){
	try{
	token = String(token).replace(/undefined|token=|\s|&.*/g,'');
	token = /^\d+_\w{32}$/.test(token)?token:"";
	if(token==""){
		throw new Error("设置token失败，请检查填写的token值")
		}

	let fl = Map("fl")==""?"9,10":Map("fl");
	let bq = Map("bq")==""?"5":Map("fl");	
	let at = source.getLoginInfoMap().get("作者列表（作者::作者id 注意添加多个用空格分割）");
	at = String((at==""||at==null)?"":at);
	let color = source.getLoginInfoMap().get("边框颜色,文字颜色[如#ffffff,#eeeeee]英文逗号，点✓完成设置");
	color = String((color == "" || color == null) ? "#B2B2B2,#707070" : color);

	let classNum = Map("classNum")
	setHeaders(
		`token=${token}`,
		`fl=${fl}`,
		`bq=${bq}`,
		`color=${color}`,
		`classNum=${classNum}`,
		`at=${java.base64Encode(at)}`
	)
}catch(e){
	java.log(e);
	}
}

