function login(b) {
   if (b == undefined) {
  	let x=source.getLoginInfoMap().get("页数")
  	v = String(source.getVariable()).split(",");
  	v[2]=x
  	source.setVariable(v.join(","));
  	return true;
  	}
  info = result
  so = getUrl();
  pw = info.密码

  if (b) {
    url = so + 'login,'
    body = `loginName=${info.邮箱}&password=${pw}&platform=`
  }  
   post = JSON.stringify({
    "body": String(body),
    "method": "POST"
  })
  url += post
  result = java.ajax(url)
  //java.log(result)
  if (result.match(/success/)){
  	p=String(result).replace(/.*nickName\"\:\"(.*?)\"?\,.*/,'$1')
  	  java.toast('🧾'+p+'🧾'+'登录成功')
  java.log(url)
  }
  c = cookie.getCookie(getUrl());
  source.putLoginHeader(c);
  }

