loginUrl:
ttt = '　　　　　　　　　　\n\n\n\n\n\n\n\n\n\n';
function login(text){
		if(text==undefined)return;
		java.log(text+ttt)
		java.toast(text+ttt)
	}
function set(text){
		return source.setVariable(text)
	}



//初始化
v=source.getVariable()
if(v==""||v==null)set("http://m.damaoli.com");



//查看接口
function look(){
		url = String(source.getVariable()).split('»')[0]
		return login('【查看搜索接口】\n\n\n　　当前书源接口为 【'+name(url)+'】');
	}



//选择接口
function $(uri){
		url = "http://m."+uri
		v=String(source.getVariable()).split('»')
if(url==v[0]){
		login('【无法选中提示】\n\n\n　　正在使用此接口，无需更换')
		return set(v[0])
	}
		login('【确定选择提示】\n\n\n　　确定选择 【'+name(url)+'】 为当前搜索接口吗？\n\n\n　　点击上方 【确定启用接口】 按钮启用')
		return set(v[0]+'»'+url)
}



//确定启用
function yes(){
		url = String(source.getVariable()).split('»')[1]
if(url==undefined){
		return login('【启用失败提示】\n\n\n　　启用失败，没有选中接口')
	}
else{
		login('【启用成功提示】\n\n\n　　启用成功，已选择 【'+name(url)+'】 为当前搜索接口')
		return set(url)
	}
}



//获取接口名称
function name(url){
		return /damaoli/.test(url)?'接口♳(笔书阁)':
		/nshkedu/.test(url)?'接口♴(文趣阁)':
		/dushuapp/.test(url)?'接口♵(读书阁)':
		'接口♶(嗨小说)';
	}

